    <script>
        $('[data-fancybox-ban]').fancybox({
            caption: function(instance, item) {
                var caption, link;

                if (item.type === 'image') {
                    caption = $(this).data('caption');
                    link = '<a href="' + item.src + '">Скачать</a>';

                    return (caption ? caption + '<br />' : '') + link;
                }
            }
        });
    </script>
